import React, {useState} from 'react'

import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap-icons/font/bootstrap-icons.css'
import './App.css'

import TaskInput from './component/RecipeInput'
import TaskTable from './component/RecipeTable'


export default function App() {

  const [recipes, setRecipes] = useState([])

  function onTaskCreated(recipe){
    const newRecipes = []
    for (let r of recipes) {newRecipes.push(r)}
    newRecipes.push(recipe)
    setRecipes(newRecipes)
  }


  function onRecipeRemove(recipe){
    const filteredrecipes = recipes.filter((r) => {
      return r.id !== recipes.id
    })

    setRecipes(filteredrecipes)
  }

  return (
    <body>
    <div className='container mt-4'>
      <div className='card card-body text-center'>

        <h1>Grandma's Recipe Book:</h1>
        <hr></hr>
        <div>Recipes:</div>

      <TaskInput onTaskCreated={onTaskCreated}></TaskInput>

      <TaskTable onRecipeRemove={onRecipeRemove} recipes={recipes}></TaskTable>

      </div>

    </div>
    </body>

  )
}
